// Greeting.jsx
import React from 'react';
import bird from '../assets/bird.png';

/*
function Itemdesc(props) {
  return (
    <h1>Hello, {props.name}! Welcome to React.</h1>
  );
}
*/
function Chdg() {
  return (
    <p className="bg-green-400 text-md font-bold pt-2 pb-4 w-64 pl-4">
      My favorite Customers
    </p>
  );
}


const list = [
  { id: 1, customer: 'Arnie', item: 'shirt-m', cost: 79.99, itempic: 4 },
  { id: 2, customer: 'Ben', item: 'shirt-lg', cost: 69.99, itempic: 4 },
  {
    id: 3,
    customer: 'Jack',
    item: 'attire',
    cost: 200.99,
    itempic: 2,
  },
  {
    id: 4,
    customer: 'Jack',
    item: 'pants',
    cost: 55.99,
    itempic: 3,
  },
];

const CustomerList = ({ onImageClick }) => {
  // Helper function to get image URL by itempic
  const getImageUrl = (itempic) => {
    const found = nlist.find(item => item.keyn === itempic);
    return found ? found.value : '';
  };

  return (
    <div className="overflow-x-auto">
      <table className="min-w-full divide-y divide-gray-200">
        <thead className="bg-gray-50">
          <tr>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">ID</th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Customer</th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Item</th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Cost</th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Item Pic</th>
          </tr>
        </thead>
        <tbody className="bg-white divide-y divide-gray-200">
          {list.map((item) => {
            const imageUrl = getImageUrl(item.itempic);
            return (
              <tr key={item.id} className="hover:bg-gray-50">
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{item.id}</td>
                <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{item.customer}</td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{item.item}</td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">${item.cost.toFixed(2)}</td>
                <td className="px-6 py-4 whitespace-nowrap">
                  {imageUrl && (
                    <button 
                      onClick={() => onImageClick(item.itempic)}
                      className="hover:opacity-75 transition-opacity"
                    >
                      <img 
                        src={imageUrl} 
                        alt={`${item.item} preview`} 
                        className="h-12 w-12 object-cover rounded"
                      />
                    </button>
                  )}
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

const Itemdesc = ({ selectedPic }) => {
  // Find the image URL based on selectedPic
  const selectedImage = nlist.find(item => item.keyn === selectedPic)?.value || '';

  return (
    <div className="pt-10 ml-10 h-96 w-96 flex items-center justify-center">
      {selectedImage ? (
        <img
          src={selectedImage}
          alt="Selected item preview"
          className="max-h-full max-w-full object-contain"
        />
      ) : (
        <p className="text-gray-400">Select an item to view</p>
      )}
    </div>
  );
};

const App = () => {
  const [selectedItemPic, setSelectedItemPic] = React.useState(null);
  const [showItemDesc, setShowItemDesc] = React.useState(false);

  const handleImageClick = (itemPic) => {
    setSelectedItemPic(itemPic);
    setShowItemDesc(true);
  };

  return (
    <div className="min-h-screen bg-green-500 p-8">
      <Chdg />
      <div className="flex">
        <CustomerList onImageClick={handleImageClick} />
        {showItemDesc && selectedItemPic && (
          <Itemdesc selectedPic={selectedItemPic} />
        )}
      </div>
    </div>
  );
};

const pic1 =
  bird;

const pic2 =
  'https://stackblitz.com/files/tailwindcss-aipmul/github/kbwoodall/tailwindcss-aipmul/main/golfPants.jpg';

const pic3 =
  'https://stackblitz.com/files/tailwindcss-aipmul/github/kbwoodall/tailwindcss-aipmul/main/justPants.jpg';

const pic4 =
  'https://stackblitz.com/files/tailwindcss-aipmul/github/kbwoodall/tailwindcss-aipmul/main/golfShirt.jpg';

const nlist = [
  { keyn: 1, value: pic1 },
  { keyn: 2, value: pic2 },
  { keyn: 3, value: pic3 },
  { keyn: 4, value: pic4 },
];

export { CustomerList, App };
export default Itemdesc;
